import React, { useContext, useState } from "react";
import { CartContext } from "../contexts/CartContext";
import "./Customizer.css";
import CartButton from "../components/CartButton";
import { useNavigate } from "react-router-dom";

const iconOptions = [
  { id: "ac", label: "❄️ AC" },
  { id: "fan", label: "🌀 Fan" },
  { id: "light", label: "💡 Light" },
  { id: "tv", label: "📺 TV" },
];

const GridCell = ({ index, onClick, children }) => (
  <div
    onClick={() => onClick(index)}
    style={{
      width: "30%",
      height: "100px",
      display: "inline-block",
      textAlign: "center",
      background: "transparent",
      margin: "5px",
      position: "relative",
      boxSizing: "border-box",
      verticalAlign: "top",
    }}
  >
    {children}
  </div>
);

function X2VCustomizer() {
  const { addToCart } = useContext(CartContext);
  const [selectedIcon, setSelectedIcon] = useState(null);
  const [placedIcons, setPlacedIcons] = useState([]);
  const [iconTexts, setIconTexts] = useState({});
  const navigate = useNavigate();

  const handlePlaceIcon = (cellIndex) => {
    const isOccupied = placedIcons.some((icon) => icon.position === cellIndex);
    if (isOccupied || selectedIcon === null) return;

    const iconPosition = {
      id: Date.now(),
      iconId: selectedIcon.id,
      label: selectedIcon.label,
      position: cellIndex,
    };

    setPlacedIcons((prev) => [...prev, iconPosition]);
    setSelectedIcon(null);
  };

  const handleIconClick = (icon) => {
    setSelectedIcon(icon);
  };

  const handleTextChange = (e, cellIndex) => {
    const newText = e.target.value;
    setIconTexts((prev) => ({
      ...prev,
      [cellIndex]: newText,
    }));
  };

  const handleDeleteIcon = (id) => {
    setPlacedIcons((prev) => prev.filter((icon) => icon.id !== id));
  };

  const handleAddToCart = () => {
    const design = {
      type: "X2V",
      icons: Array.from({ length: 11 })
        .map((_, index) => {
          const icon = placedIcons.find((i) => i.position === index);
          return {
            iconId: icon?.iconId || null,
            label: icon?.label || "",
            position: index,
            text: iconTexts[index] || "",
          };
        })
        .filter((entry) => entry.iconId || entry.text),
      quantity: 1,
    };
    addToCart(design);
  };

  return (
    <div className="customizer-container">
      <div style={{ position: "absolute", top: 20, right: 30 }}>
        <CartButton />
      </div>

      <h2>Customize your X2V Panel</h2>

      <div style={{ display: "flex", gap: "10px", marginBottom: "20px" }}>
        {iconOptions.map((icon) => (
          <div
            key={icon.id}
            onClick={() => handleIconClick(icon)}
            style={{
              padding: "10px",
              background: "#e0e0e0",
              borderRadius: "8px",
              cursor: "pointer",
            }}
          >
            {icon.label}
          </div>
        ))}
      </div>

      <div
        style={{
          width: "500px",
          background: "#f0f0f0",
          padding: "10px",
          border: "2px solid #ccc",
        }}
      >
        <h4>Small Icons Grid (3x3)</h4>
        <div style={{ display: "flex", flexWrap: "wrap" }}>
          {Array.from({ length: 9 }).map((_, index) => (
            <GridCell key={index} index={index} onClick={handlePlaceIcon}>
              {(() => {
                const icon = placedIcons.find((i) => i.position === index);
                const text = iconTexts[index];
                return (
                  <div
                    style={{
                      display: "flex",
                      flexDirection: "column",
                      alignItems: "center",
                      justifyContent: "center",
                      position: "relative",
                      height: "100%",
                    }}
                  >
                    {icon && (
                      <div style={{ fontSize: "24px" }}>{icon.label}</div>
                    )}
                    {text && (
                      <div style={{ fontSize: "17px", marginTop: "5px" }}>
                        {text}
                      </div>
                    )}
                    {icon && (
                      <button
                        onClick={() => handleDeleteIcon(icon.id)}
                        style={{
                          position: "absolute",
                          top: "-5px",
                          right: "-5px",
                          width: "20px",
                          height: "20px",
                          borderRadius: "50%",
                          backgroundColor: "red",
                          color: "white",
                          fontSize: "14px",
                          border: "none",
                          cursor: "pointer",
                          display: "flex",
                          alignItems: "center",
                          justifyContent: "center",
                          padding: 0,
                        }}
                      >
                        −
                      </button>
                    )}
                  </div>
                );
              })()}
              <input
                type="text"
                value={iconTexts[index] || ""}
                onChange={(e) => handleTextChange(e, index)}
                placeholder="Add text"
                style={{
                  position: "absolute",
                  bottom: "5px",
                  left: "5px",
                  width: "90%",
                  fontSize: "12px",
                  textAlign: "center",
                }}
              />
            </GridCell>
          ))}
        </div>

        <h4 style={{ marginTop: "30px" }}>Large Icons Grid (1x2)</h4>
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          {Array.from({ length: 2 }).map((_, i) => {
            const index = 9 + i;
            const icon = placedIcons.find((i) => i.position === index);
            const text = iconTexts[index];

            return (
              <div
                key={index}
                onClick={() => handlePlaceIcon(index)}
                style={{
                  width: "50%",
                  height: "200px",
                  backgroundColor: "#ffffff",
                  margin: "10px 0",
                  position: "relative",
                  border: "1px solid #999",
                }}
              >
                <div
                  style={{
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                    justifyContent: "center",
                    height: "100%",
                    position: "relative",
                  }}
                >
                  {icon && (
                    <div style={{ fontSize: "48px", fontWeight: "bold" }}>
                      {icon.label}
                    </div>
                  )}
                  {text && (
                    <div style={{ fontSize: "20px", marginTop: "5px" }}>
                      {text}
                    </div>
                  )}
                  {icon && (
                    <button
                      onClick={() => handleDeleteIcon(icon.id)}
                      style={{
                        position: "absolute",
                        top: "-5px",
                        right: "-5px",
                        width: "20px",
                        height: "20px",
                        borderRadius: "50%",
                        backgroundColor: "red",
                        color: "white",
                        fontSize: "14px",
                        border: "none",
                        cursor: "pointer",
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        padding: 0,
                      }}
                    >
                      −
                    </button>
                  )}
                </div>

                <input
                  type="text"
                  value={iconTexts[index] || ""}
                  onChange={(e) => handleTextChange(e, index)}
                  placeholder="Add text"
                  style={{
                    position: "absolute",
                    bottom: "5px",
                    left: "5px",
                    width: "90%",
                    fontSize: "12px",
                    textAlign: "center",
                  }}
                />
              </div>
            );
          })}
        </div>
      </div>

      <div style={{ marginTop: 20, display: "flex", gap: "10px", justifyContent: "center" }}>
  <button onClick={() => navigate("/")} style={{ padding: "10px 15px" }}>
    Back to Panel Selection
  </button>
  <button onClick={() => navigate("/subtypes/extended")} style={{ padding: "10px 15px" }}>
    Back to Extended Panels
  </button>
  <button
    onClick={handleAddToCart}
    style={{ padding: "10px 15px", backgroundColor: "#4CAF50", color: "white", border: "none", cursor: "pointer" }}
  >
    Add to Cart
  </button>
</div>
    </div>
  );
}
export default X2VCustomizer;
